import React, {Component} from "react";
export default class Login extends Component{

  constructor(props){
    super(props);
      this.state={
        username:"",
        password:"",
        errorMessage:"",
        successMessage:""
      }
      this.reset=this.reset.bind(this);
  }

  setValue(e){
    let id=e.target.id;
    let value=e.target.value;
    let username=e.target.username;
    if(value===undefined || value==="")
      return;
    if(id==="username"){
      username=value.toUpperCase();
      this.setState({
        username:username})
    }
    else{
      this.setState({
        password:e.target.value})
    }
  }

  submit(e){
    if(this.state.username===undefined||this.state.username===""
        || this.state.password===undefined || this.state.password===""){
      this.setState({
        errorMessage:"Debe ingresar email y contraseña correctamente"})
        }
    else{
      this.setState({
        successMessage:"Bienvenido a CryptoStockZ"})
    }
  }
  reset(){
    let username=this.state.username;
    let password=this.state.password;
      this.setState({
        username:"",
        password:"",
      })
  }
  render(){
    return(
      <div>
        Username: <input type="text" id="username" value={this.state.username} onChange={(e)=>this.setValue(e)} />
        Password: <input type="password" id="password" value={this.state.password} onChange={(e)=>this.setValue(e)} />
      <p>{this.state.errorMessage}</p>
        <p>{this.state.successMessage}</p>
        <button onClick={(e)=>this.submit(e)}>Iniciar Sesión</button>
        <button onClick={this.reset}>Reset </button>
      </div>
    )
  }
}
