import React, { useState } from "react";
import {useHistory, Link} from "react-router-dom";
import {
  Button,
  FormGroup,
  FormControl,
  ControlLabel
} from "react-bootstrap";
import { useFormFields } from "../libs/hooksLib";
import "./SignUp.css";
var ok_reg;
export {ok_reg};
var params;

export default function Signup() {
  const history = useHistory();
  const [fields, handleFieldChange] = useFormFields({
    username: "",
    name: "",
    email: "",
    password: "",
    roles: "user",
    metamaskAccount: ""
  });
  const [newUser, setNewUser] = useState(null);

  function validateForm() {
    return (
      fields.email.length > 0 &&
      fields.password.length > 0
    );
  }

  async function handleSubmit(event) {
    event.preventDefault();
    makePostRequest();

    setNewUser("test");
    ok_reg= true
    history.push("/")


  }

  function renderForm() {
    return (
      <form onSubmit={handleSubmit}>
      <FormGroup controlId="username" bsSize="large">
        <ControlLabel>Username</ControlLabel>
        <FormControl
          autoFocus
          type="username"
          value={fields.username}
          onChange={handleFieldChange}
        />
      </FormGroup>
      <FormGroup controlId="name" bsSize="large">
        <ControlLabel>Name</ControlLabel>
        <FormControl
          autoFocus
          type="name"
          value={fields.name}
          onChange={handleFieldChange}
        />
        <FormGroup controlId="email" bsSize="large">
          <ControlLabel>Email</ControlLabel>
          <FormControl
            autoFocus
            type="email"
            value={fields.email}
            onChange={handleFieldChange}
          />
        </FormGroup>
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <ControlLabel>Password</ControlLabel>
          <FormControl
            type="password"
            value={fields.password}
            onChange={handleFieldChange}
          />
        </FormGroup>

          <FormGroup controlId="lista" bsSize="large">
        <select id="lista" name="lista" size="1">
        <option value="fabricante">Manufacturer</option>
        <option value="usuario">User</option>
        </select>
      </FormGroup>

      <FormGroup controlId="metamask" bsSize="large">
        <ControlLabel>Cuenta Metamask</ControlLabel>
        <FormControl
          autoFocus
          type="metamask"
          value={fields.metamask}
          onChange={handleFieldChange}
        />
        </FormGroup>

        <Button block bsSize="large" disabled={!validateForm()} type="submit">
        <Link to="/" className="nav-perfil">
          Registrar
          </Link>
        </Button>
      </form>
    );
  }

  const axios = require('axios');

  async function makePostRequest() {

      params = {
          username: fields.username,
          name: fields.name,
          email: fields.email,
          password: fields.password,
          roles: "user",
          metamaskAccount: fields.metamask
        }

      let res = await axios.post('http://localhost:10010/signup', params);

      console.log(res.data);
  }



  return (
    <div className="Signup">
    {newUser === null ? renderForm():renderForm()}
    </div>
  );
}
